<!--            </li>-->
            <li v-for="(item,index) in conditionList">
                <span>{{item.title}}</span>
                <span v-for="ite in item.options" @click="chooseCondition(index,ite)"
                      :class="chooseConditionList[index]===ite?'cur':''">{{ite}}</span>
            </li>
        </ul>
    </div>
</template>
<script>
    export default{
        name: 'condition',
        data() {
            return {
                conditionList: [],
                chooseConditionList: ['不限','不限','不限','不限','不限']
            }
        },
        methods: {
            getConditionList() {
                this.$axios({
                    url: '/getConditionList',
                    methods: 'get',
                }).then(res=>{
                    if(res.code===0) {
                        this.conditionList=res.data;
                    }
                })
            },
            chooseCondition(index,ite) {
                this.$set(this.chooseConditionList, index, ite)
            }
        },
        watch: {

        },
        mounted () {
            this.getConditionList()
        }
    }
</script>
